import React, { useEffect, useState, Fragment } from 'react';
import '../styles/index.css';
import { Menu, Transition } from '@headlessui/react';
import { HomeIcon, ChartBarIcon, UserCircleIcon, ArrowRightOnRectangleIcon } from '@heroicons/react/24/outline';
import { TrashIcon, PencilSquareIcon, XMarkIcon, CheckIcon } from '@heroicons/react/24/solid';
import { getStocks, getPortfolio, addToPortfolio, editPortfolioEntry, deletePortfolioEntry } from '../api/api';

function StockList() {
  // Placeholder content; replace with your actual implementation
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    getStocks()
      .then(data => {
        setStocks(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to load stocks');
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="p-4">Loading stocks...</div>;
  if (error) return <div className="p-4 text-red-500">{error}</div>;

  return (
    <div className="bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Stock List</h2>
      <table className="min-w-full table-auto border-collapse">
        <thead>
          <tr>
            <th className="border-b px-4 py-2 text-left">Symbol</th>
            <th className="border-b px-4 py-2 text-left">Name</th>
            <th className="border-b px-4 py-2 text-left">Price</th>
          </tr>
        </thead>
        <tbody>
          {stocks.map(stock => (
            <tr key={stock.symbol}>
              <td className="border-b px-4 py-2">{stock.symbol}</td>
              <td className="border-b px-4 py-2">{stock.name}</td>
              <td className="border-b px-4 py-2">${stock.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default StockList;
