import React, { useEffect, useState, Fragment } from 'react';
import '../styles/index.css';
import { Menu, Transition } from '@headlessui/react';
import { HomeIcon, ChartBarIcon, UserCircleIcon, ArrowRightOnRectangleIcon } from '@heroicons/react/24/outline';
import { TrashIcon, PencilSquareIcon, XMarkIcon, CheckIcon } from '@heroicons/react/24/solid';
import { getStocks, getPortfolio, addToPortfolio, editPortfolioEntry, deletePortfolioEntry } from '../api/api';

function StockList() {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    getStocks()
      .then(data => {
        setStocks(data);
        setLoading(false);
      })
      .catch(err => {
        setError('Failed to load stocks');
        setLoading(false);
      });
  }, []);

  if (loading) return <div className="p-4 text-blue-700">Loading stocks...</div>;
  if (error) return <div className="p-4 text-red-500">{error}</div>;

  return (
    <div className="bg-white p-8 rounded-2xl shadow-lg">
      <h2 className="text-3xl font-extrabold mb-6 text-blue-900">Stock List</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full table-auto border-collapse rounded-xl overflow-hidden">
          <thead className="bg-blue-100">
            <tr>
              <th className="border-b px-6 py-3 text-left text-blue-900 font-semibold">Symbol</th>
              <th className="border-b px-6 py-3 text-left text-blue-900 font-semibold">Name</th>
              <th className="border-b px-6 py-3 text-left text-blue-900 font-semibold">Price</th>
            </tr>
          </thead>
          <tbody>
            {stocks.map((stock, idx) => (
              <tr key={stock.symbol} className={idx % 2 === 0 ? 'bg-white' : 'bg-blue-50 hover:bg-blue-100'}>
                <td className="border-b px-6 py-3 font-mono text-blue-800">{stock.symbol}</td>
                <td className="border-b px-6 py-3">{stock.name}</td>
                <td className="border-b px-6 py-3">${stock.price}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default StockList;
