import React, { useEffect, useState, Fragment } from 'react';
import './index.css';
import { Menu, Transition } from '@headlessui/react';
import { HomeIcon, ChartBarIcon, UserCircleIcon, ArrowRightOnRectangleIcon } from '@heroicons/react/24/outline';
import { TrashIcon, PencilSquareIcon, XMarkIcon, CheckIcon } from '@heroicons/react/24/solid';

const menuItems = [
  { icon: <HomeIcon className="w-6 h-6" />, label: 'Dashboard', active: false },
  { icon: <ChartBarIcon className="w-6 h-6" />, label: 'Stocks', active: true },
  { icon: <UserCircleIcon className="w-6 h-6" />, label: 'Profile', active: false },
  { icon: <ArrowRightOnRectangleIcon className="w-6 h-6" />, label: 'Logout', active: false },
];

function StockList() {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [portfolio, setPortfolio] = useState([]);
  const [form, setForm] = useState({ symbol: '', quantity: 1 });
  const [portfolioError, setPortfolioError] = useState(null);
  const [editId, setEditId] = useState(null);
  const [editForm, setEditForm] = useState({ symbol: '', quantity: 1 });

  useEffect(() => {
    fetch('http://localhost:8080/getStocks')
      .then((res) => {
        if (!res.ok) throw new Error('Network response was not ok');
        return res.json();
      })
      .then((data) => {
        setStocks(data);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setLoading(false);
      });
    fetchPortfolio();
  }, []);

  const fetchPortfolio = () => {
    fetch('http://localhost:8080/portfolio')
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setPortfolio(data);
        } else {
          setPortfolioError('Failed to load portfolio.');
          setPortfolio([]);
        }
      })
      .catch(() => {
        setPortfolioError('Failed to load portfolio.');
        setPortfolio([]);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setForm(f => ({ ...f, [name]: value }));
  };

  const handleAddStock = (e) => {
    e.preventDefault();
    setPortfolioError(null);
    const stock = stocks.find(s => s.symbol === form.symbol);
    if (!stock) {
      setPortfolioError('Stock symbol not found.');
      return;
    }
    fetch('http://localhost:8080/portfolio', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        symbol: form.symbol,
        quantity: parseInt(form.quantity, 10),
        price: stock.price
      })
    })
      .then(res => {
        if (!res.ok) throw new Error('Failed to add to portfolio');
        return res.json();
      })
      .then(data => {
        setPortfolio(data);
        setForm({ symbol: '', quantity: 1 });
      })
      .catch(() => setPortfolioError('Failed to add to portfolio.'));
  };

  const handleEditClick = (entry) => {
    setEditId(entry.id);
    setEditForm({ symbol: entry.symbol, quantity: entry.quantity });
  };

  const handleEditInputChange = (e) => {
    const { name, value } = e.target;
    setEditForm(f => ({ ...f, [name]: value }));
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:8080/portfolio/edit', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        id: editId,
        symbol: editForm.symbol,
        quantity: parseInt(editForm.quantity, 10)
      })
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setPortfolio(data);
          setEditId(null);
        } else {
          setPortfolioError('Failed to edit portfolio entry.');
        }
      });
  };

  const handleDelete = (id) => {
    if (!id) {
      setPortfolioError('Delete failed: No ID provided.');
      return;
    }
    fetch('http://localhost:8080/portfolio/delete', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ id })
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setPortfolio(data);
        } else {
          setPortfolioError('Failed to delete portfolio entry.');
        }
      })
      .catch(() => setPortfolioError('Failed to delete portfolio entry.'));
  };

  return (
    <div className="min-h-screen flex bg-gradient-to-br from-blue-50 to-pink-100">
      {/* Sidebar */}
      <aside className="w-20 bg-white shadow-2xl flex flex-col items-center py-6 space-y-4 rounded-tr-3xl rounded-br-3xl border-r border-blue-100">
        <div className="mb-8 mt-2">
          <span className="text-blue-700 text-3xl font-extrabold tracking-tight">S</span>
        </div>
        {menuItems.map((item, idx) => (
          <button
            key={item.label}
            className={`flex flex-col items-center w-12 py-2 rounded-xl transition-all duration-200 focus:outline-none ${item.active ? 'bg-blue-100 text-blue-700 shadow' : 'text-gray-400 hover:text-blue-600 hover:bg-blue-50'}`}
          >
            {item.icon}
            <span className="text-[10px] mt-1 font-semibold tracking-wide">{item.label}</span>
          </button>
        ))}
      </aside>
      {/* Main content */}
      <div className="flex-1 flex flex-col min-w-0">
        {/* Top bar */}
        <header className="h-16 bg-white shadow flex items-center px-8 justify-between border-b border-blue-100">
          <div className="flex items-center gap-3">
            <span className="text-blue-700 text-2xl font-extrabold tracking-tight">Stocks</span>
            <span className="text-gray-400 text-lg">|</span>
            <span className="text-blue-400 font-semibold tracking-wide">Dashboard</span>
          </div>
          <div className="flex items-center gap-4">
            <input
              type="text"
              placeholder="Search stocks..."
              className="rounded-lg border border-blue-100 px-3 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-200 bg-blue-50 placeholder:text-blue-300"
              style={{ minWidth: 180 }}
              disabled
            />
            <Menu as="div" className="relative inline-block text-left">
              <div>
                <Menu.Button className="flex items-center gap-2 focus:outline-none">
                  <img src="https://i.pravatar.cc/32" alt="avatar" className="rounded-full w-8 h-8 border-2 border-blue-200" />
                  <span className="text-gray-600 font-medium">User</span>
                </Menu.Button>
              </div>
              <Transition as={Fragment} enter="transition ease-out duration-100" enterFrom="transform opacity-0 scale-95" enterTo="transform opacity-100 scale-100" leave="transition ease-in duration-75" leaveFrom="transform opacity-100 scale-100" leaveTo="transform opacity-0 scale-95">
                <Menu.Items className="absolute right-0 mt-2 w-40 origin-top-right divide-y divide-gray-100 rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none z-50">
                  <div className="px-1 py-1">
                    <Menu.Item>
                      {({ active }) => (
                        <button className={`$ {active ? 'bg-blue-100 text-blue-700' : 'text-gray-700'} group flex w-full items-center rounded-md px-2 py-2 text-sm`}>Profile</button>
                      )}
                    </Menu.Item>
                    <Menu.Item>
                      {({ active }) => (
                        <button className={`$ {active ? 'bg-blue-100 text-blue-700' : 'text-gray-700'} group flex w-full items-center rounded-md px-2 py-2 text-sm`}>Logout</button>
                      )}
                    </Menu.Item>
                  </div>
                </Menu.Items>
              </Transition>
            </Menu>
          </div>
        </header>
        {/* Content */}
        <main className="flex-1 flex items-start justify-center bg-transparent">
          <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-2xl mt-10 border border-blue-100">
            <h2 className="text-xl font-bold text-blue-700 mb-6 flex items-center gap-2">
              <span role="img" aria-label="stocks">📈</span> Stock List
            </h2>
            {/* Add Stock to Portfolio Form */}
            <form onSubmit={handleAddStock} className="mb-6 flex gap-4 items-end">
              <div>
                <label className="block text-blue-700 font-semibold mb-1">Symbol</label>
                <select name="symbol" value={form.symbol} onChange={handleInputChange} className="border rounded px-2 py-1">
                  <option value="">Select</option>
                  {stocks.map(stock => (
                    <option key={stock.symbol} value={stock.symbol}>{stock.symbol}</option>
                  ))}
                </select>
              </div>
              <div>
                <label className="block text-blue-700 font-semibold mb-1">Quantity</label>
                <input type="number" name="quantity" min="1" value={form.quantity} onChange={handleInputChange} className="border rounded px-2 py-1 w-20" />
              </div>
              <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded font-bold hover:bg-blue-700">Add to Portfolio</button>
            </form>
            {portfolioError && <div className="text-red-600 mb-4">{portfolioError}</div>}
            {/* Portfolio Table */}
            <h3 className="text-lg font-bold text-blue-600 mb-2">My Portfolio</h3>
            <div className="overflow-x-auto mb-8">
              <table className="w-full text-left border-separate border-spacing-y-2">
                <thead>
                  <tr>
                    <th className="bg-blue-200 text-blue-900 px-4 py-2 rounded-tl-xl">Symbol</th>
                    <th className="bg-blue-200 text-blue-900 px-4 py-2">Quantity</th>
                    <th className="bg-blue-200 text-blue-900 px-4 py-2">Price</th>
                    <th className="bg-blue-200 text-blue-900 px-4 py-2 rounded-tr-xl">Amount</th>
                    <th className="bg-blue-200 text-blue-900 px-4 py-2 rounded-tr-xl">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {portfolio.length === 0 ? (
                    <tr><td colSpan="5" className="text-blue-400 px-4 py-2">No stocks in portfolio.</td></tr>
                  ) : (
                    portfolio.map((entry, idx) => (
                      <tr key={entry.id || idx} className="hover:bg-blue-50 transition">
                        {editId === entry.id ? (
                          <>
                            <td className="px-4 py-2 font-semibold text-blue-900">
                              <select name="symbol" value={editForm.symbol} onChange={handleEditInputChange} className="border rounded px-2 py-1">
                                {stocks.map(stock => (
                                  <option key={stock.symbol} value={stock.symbol}>{stock.symbol}</option>
                                ))}
                              </select>
                            </td>
                            <td className="px-4 py-2 text-blue-700">
                              <input type="number" name="quantity" min="1" value={editForm.quantity} onChange={handleEditInputChange} className="border rounded px-2 py-1 w-20" />
                            </td>
                            <td className="px-4 py-2 text-blue-700">${entry.price?.toLocaleString(undefined, {minimumFractionDigits: 2})}</td>
                            <td className="px-4 py-2 text-blue-700">${(entry.price && editForm.quantity ? (entry.price * editForm.quantity).toLocaleString(undefined, {minimumFractionDigits: 2}) : '0.00')}</td>
                            <td className="px-4 py-2 flex gap-2">
                              <button onClick={handleEditSubmit} className="bg-green-500 hover:bg-green-600 text-white p-1 rounded" title="Save"><CheckIcon className="w-5 h-5" /></button>
                              <button onClick={() => setEditId(null)} className="bg-gray-300 hover:bg-gray-400 text-gray-700 p-1 rounded" title="Cancel"><XMarkIcon className="w-5 h-5" /></button>
                            </td>
                          </>
                        ) : (
                          <>
                            <td className="px-4 py-2 font-semibold text-blue-900">{entry.symbol}</td>
                            <td className="px-4 py-2 text-blue-700">{entry.quantity}</td>
                            <td className="px-4 py-2 text-blue-700">${entry.price?.toLocaleString(undefined, {minimumFractionDigits: 2})}</td>
                            <td className="px-4 py-2 text-blue-700">${(entry.price && entry.quantity ? (entry.price * entry.quantity).toLocaleString(undefined, {minimumFractionDigits: 2}) : '0.00')}</td>
                            <td className="px-4 py-2 flex gap-2">
                              <button onClick={() => handleEditClick(entry)} className="bg-yellow-400 hover:bg-yellow-500 text-white p-1 rounded" title="Edit"><PencilSquareIcon className="w-5 h-5" /></button>
                              <button onClick={() => handleDelete(entry.id)} className="bg-red-500 hover:bg-red-600 text-white p-1 rounded" title="Delete"><TrashIcon className="w-5 h-5" /></button>
                            </td>
                          </>
                        )}
                      </tr>
                    ))
                  )}
                </tbody>
                {portfolio.length > 0 && (
                  <tfoot>
                    <tr>
                      <td colSpan="4" className="text-right font-bold text-blue-900 px-4 py-2">Total</td>
                      <td className="text-blue-900 font-bold px-4 py-2">
                        ${portfolio.reduce((sum, entry) => sum + ((entry.price && entry.quantity) ? entry.price * entry.quantity : 0), 0).toLocaleString(undefined, {minimumFractionDigits: 2})}
                      </td>
                    </tr>
                  </tfoot>
                )}
              </table>
            </div>
            <h2 className="text-xl font-bold text-blue-700 mb-6 flex items-center gap-2">
              <span role="img" aria-label="stocks">📈</span> Stock List
            </h2>
            {loading && <div className="text-blue-600 text-lg">Loading stocks...</div>}
            {error && <div className="text-red-600 text-lg">Error: {error}</div>}
            {!loading && !error && (
              <div className="overflow-x-auto">
                <table className="w-full text-left border-separate border-spacing-y-2">
                  <thead>
                    <tr>
                      <th className="bg-blue-600 text-white px-6 py-3 rounded-tl-xl text-lg font-semibold">Symbol</th>
                      <th className="bg-blue-600 text-white px-6 py-3 rounded-tr-xl text-lg font-semibold">Price</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stocks.map(stock => (
                      <tr key={stock.symbol} className="hover:bg-blue-50 transition">
                        <td className="px-6 py-4 font-semibold text-blue-900">{stock.symbol}</td>
                        <td className="px-6 py-4 text-blue-700">${stock.price.toLocaleString(undefined, {minimumFractionDigits: 2})}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </main>
        <footer className="text-center text-gray-400 mt-8 mb-4 text-xs select-none">
          &copy; {new Date().getFullYear()} MyStocks. Powered by <span className="font-semibold text-blue-700">Spring Boot</span> & <span className="font-semibold text-blue-700">React</span>
        </footer>
      </div>
    </div>
  );
}

export default StockList;
