import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import './index.css';
import StockList from './StockList';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const menuOptions = [
  { label: 'Dashboard', key: 'dashboard' },
  { label: 'Stocks', key: 'stocks' },
  { label: 'Portfolio', key: 'portfolio' },
];

export default function Dashboard() {
  const [portfolio, setPortfolio] = useState([]);
  const [activeTab, setActiveTab] = useState('dashboard');

  useEffect(() => {
    fetch('http://localhost:8080/portfolio')
      .then(res => res.json())
      .then(data => setPortfolio(data));
  }, []);

  const chartData = {
    labels: portfolio.map(entry => entry.symbol),
    datasets: [
      {
        label: 'Value',
        data: portfolio.map(entry => entry.quantity * entry.price),
        backgroundColor: 'rgba(59, 130, 246, 0.5)',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: 'top' },
      title: { display: true, text: 'Portfolio Value by Stock' },
    },
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-48 bg-gray-800 text-white flex flex-col p-4">
        <div className="text-2xl font-bold mb-8">Menu</div>
        {menuOptions.map(opt => (
          <button
            key={opt.key}
            className={`text-left py-2 px-4 rounded mb-2 ${activeTab === opt.key ? 'bg-blue-600' : 'hover:bg-gray-700'}`}
            onClick={() => setActiveTab(opt.key)}
          >
            {opt.label}
          </button>
        ))}
      </div>
      {/* Main Content */}
      <div className="flex-1 p-8 bg-gray-100">
        {activeTab === 'dashboard' && (
          <div>
            <h1 className="text-3xl font-bold mb-6">Portfolio Dashboard</h1>
            <div className="bg-white p-6 rounded shadow">
              <Bar data={chartData} options={chartOptions} />
            </div>
          </div>
        )}
        {activeTab === 'stocks' && (
          <StockList />
        )}
        {activeTab === 'portfolio' && (
          <div>
            <h2 className="text-2xl font-bold">Portfolio</h2>
            <p>Portfolio details coming soon...</p>
          </div>
        )}
      </div>
    </div>
  );
}
